import React from 'react';


import Messages from './Messages';


const styles = {

    channel: {
        height: '100%',
        flex: '1 1 auto',
        display: 'flex',
        flexDirection: 'column',
        overflow: 'hidden',
    },
    messages: {
        flex: '1 1 auto',
        height: '100%',
        overflow: 'auto',
        '& ul': {
            'margin': 0,
            'padding': 0,
            'textIndent': 0,
            'listStyleType': 0,
        },
    },
    message: {
        margin: '.2rem',
        padding: '.2rem',
        // backgroundColor: '#66728E',
        ':hover': {
            backgroundColor: 'rgba(255,255,255,.2)',
        },
    },
}



export default () => {

    const addMessage = (message) => {
        setMessages([
            ...messages,
            message
        ])
    }

    return(
        <div className="App-channel" css={styles.channel}>
          <Messages/>
          <MessageForm addMessage={addMessage} />
        </div>            
    )
}
