import React from 'react';

export default ({addMessage, styles}) => {
    const onSubmit = (e) => {
        e.preventDefault()
        const data = new FormData(e.target)
        addMessage({
            content: data.get('content'),
            author: 'david',
            creation: Date.now(),
          })
        e.target.elements.content.value = ''
    }

    return (

        <form styles={styles.form} onSubmit={onSubmit}>
            <input type="input" name="content" styles={styles.content} />
            <input type="submit" value="Send" styles={styles.send} />
        </form>

    )
}
